let playerActions={};playerActions.addPlayer=()=>{openPlayerModal()};playerActions.editPlayer=n=>{const t=playersDataTable.row($(n).closest("tr")).data();openPlayerModal(t)};playerActions.deletePlayer=async n=>{if(confirm("Are you sure you want to delete this player?")){const t=playersDataTable.row($(n).closest("tr")).data();console.log(t.playerId);await playerApi.delete(t.playerId);showToast("Player removed","success",3e3);playersDataTable.ajax.reload()}};$("#playersTable tbody").on("click",".edit-btn",function(){playerActions.editPlayer(this)});$("#playersTable tbody").on("click",".delete-btn",async function(){await playerActions.deletePlayer(this)});