function showToast(n,t="info",i=3e3){const u={success:"bg-success text-white",error:"bg-danger text-white",warning:"bg-warning text-dark",info:"bg-info text-dark"},r=document.createElement("div");r.innerHTML=`
        <div class="toast position-fixed top-0 start-50 translate-middle-x mt-3 ${u[t]||u.info}" 
             role="alert" aria-live="assertive" aria-atomic="true" style="z-index:9999;">
            <div class="toast-body">${n}</div>
        </div>
    `;document.body.appendChild(r);const f=new bootstrap.Toast(r.querySelector(".toast"),{delay:i});f.show();f._element.addEventListener("hidden.bs.toast",()=>r.remove())}window.showToast=showToast;